CREATE TABLE usuario (login varchar(10) primary key, email varchar(30), senha varchar(10), nome varchar(100), pontos int)
CREATE TABLE topico (id_topico int primary key GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), titulo varchar(50), conteudo varchar(2000), login varchar(10), CONSTRAINT topico_login_fkey FOREIGN KEY (login) REFERENCES usuario(login))
CREATE TABLE comentario (id_comentario int primary key GENERATED ALWAYS AS IDENTITY (START WITH 1, INCREMENT BY 1), comentario varchar(2000), login varchar(10), id_topico int, CONSTRAINT comentario_login_fkey FOREIGN KEY (login) REFERENCES usuario(login), CONSTRAINT comentario_id_topico_fkey FOREIGN KEY (id_topico) REFERENCES topico(id_topico))
